import React, { Component } from 'react'
import './Counter.css'
import { tsExpressionWithTypeArguments } from '@babel/types';

class Counter extends Component {

    // Define the initial state in a constructor
    // state => counter 0

    constructor() {
        super(); // Error 1
        this.state = {
            counter: 0,
            secondCounter: 100
        }

        // Arrow function prevents the need for binding, the binding happend automatically
        // this.increment = this.increment.bind(this)
    }

    render = () => {
        return (
            <div className="counter">
                <button onClick={this.increment}>+1</button>
                <span className="count"
                    style= {{fontSize: "50px", padding: "15px 30px"}}>{this.state.counter}</span>
>
            </div>
        );
    }
    increment = () => { // Update state
        // console.log('increment');
        this.setState({
            counter: this.state.counter + 1,
            secondCounter: this.state.secondCounter + 1
        });
    }

}



export default Counter
